 INSERT INTO CAR (ID, CAR_MODEL_ID, COLOR, PRICE, URL, SHORT_INFO, LONG_INFO) VALUES (1, 1, 'BLACK', 150000, 'https://autow5minut.pl/wp-content/uploads/2017/08/mercedes_amg_cla45_381km_07.jpg', 'samochód osobowy klasy kompaktowej produkowany przez niemiecki koncern Mercedes-Benz w fabryce na Węgrzech od 2013 roku.','Auto zostało zaprezentowane w wersji produkcyjnej podczas targów motoryzacyjnych w Detroit w styczniu 2013 roku. Auto nie ma poprzednika, ani też bezpośredniego konkurenta. To sedan powstały na tej samej koncepcji co model CLS. Pojazd jest tak naprawdę wersją sedan trzeciej generacji A-klasy. Samochód powstał na bazie prototypu pod nazwą Mercedes-Benz Concept Style Coupé.
 INSERT INTO CAR (ID, CAR_MODEL_ID, COLOR, PRICE, URL, SHORT_INFO, LONG_INFO) VALUES (2, 3, 'WHITE', 350000, 'https://www.mercedes-amg.com/dam/hq/model-pages/Vehicles/S-class/Coupe/S63/Facelift/C217_ext_SSPIP77359_sd.jpg/_jcr_content/renditions/original./C217_ext_SSPIP77359_sd.jpg', 'Samochód osobowy klasy luksusowej produkowany przez niemiecką firmę Mercedes-Benz od lipca 2013 roku.', 'Pojazd został zaprezentowany 15 maja 2013 roku w Hamburgu. Stylistyka nadwozia nawiązuje do modeli CLA i CLS. Współczynnik oporu aerodynamicznego nadwozia Cx obniżono do 0,24 lub 0,23 (w zależności od wersji).Zawieszenie pojazdu oparte jest na pneumatycznym układzie Airmatic znanym z poprzednich generacji modelu. Cała karoseria wykonana została z aluminium, w porównaniu z poprzednikiem wzrosły nieznacznie wymiary nadwozia, zachowano przy tym taki sam rozstaw osi. Masa własna wzrosła o kilkadziesiąt kg. Do oświetlenia wykorzystano wyłącznie diody typu LED. Napęd przenoszony jest na oś tylną poprzez 7-biegową automatyczną skrzynię biegów G-Tronic. Pojazd oferowany jest w wersji ze standardowym oraz przedłużonym rozstawem os');
 INSERT INTO CAR (ID, CAR_MODEL_ID, COLOR, PRICE, URL, SHORT_INFO, LONG_INFO) VALUES (3, 4, 'WHITE', 250000, 'https://s.aolcdn.com/dims-global/dims3/GLOB/legacy_thumbnail/788x525/quality/85/https://s.aolcdn.com/commerce/autodata/images/USC60AUC021B021001.jpg', 'samochód osobowy klasy premium produkowany od 1994 roku przez markę Audi. Od 2018 roku produkowana jest piąta generacja pojazdu', 'Od szerokiej chłodnicy Singleframe przez dynamicznie zwężone reflektory i wyraźnie zaznaczone krawędzie karoserii aż po elegancko wykończony tył  Audi A6 Limousine każdym detalem daje jasną deklarację swojego charakteru: sportowego luksusu i najwyższej jakości.');
 INSERT INTO CAR (ID, CAR_MODEL_ID, COLOR, PRICE, URL, SHORT_INFO, LONG_INFO) VALUES (4, 6, 'YELLOW', 450000, 'https://s.aolcdn.com/commerce/autodata/images/USC50BMC641A021001.jpg', 'najnowszy model auta sportowego marki BMW serii 4, następca modelu E92 BMW M3', 'Na rynku aut sportowych zastępuje dwa bliźniacze modele; BMW M3 Coupe i BMW M3 Convertible. Chociaż samo nadwozie nie uległo dużej modyfikacji to sam producent zapewnia, iż wnętrze, sterowanie, silnik, skrzynia biegów, a także zawieszenie zostały poddane ulepszeniu. Sporo elementów zostało zastąpionych elementami wykonanymi z włókna węglowego, co zmniejsza ich zużycie oraz wagę auta.');
